    然后根据标签参数替换原来的HTML

// 获取该标签的参数
attrs := make(map[string]string)
for _, attr := range token.Attr {
    attrs[attr.Key] = attr.Val
}
// 确认是163music
if attrs[&#34;_name&#34;] == &#34;163music&#34; {
    // 获取替换的内容
    var buf bytes.Buffer
    err := tmpl.Execute(&amp;buf, attrs)
    if err != nil {
        panic(err)
    }
    w.WriteString(buf.String())
    continue
}


