        conf_file string
        print_ver bool
    )
    flag.StringVar(&amp;conf_file, &#34;c&#34;, &#34;etc/config.json&#34;, &#34;config file&#34;)
    flag.BoolVar(&amp;print_ver, &#34;v&#34;, false, &#34;config file&#34;)
