$(document).ready(function(){
    $("pre").css("max-height", $(window).height()/1.5);
    var e = $(".back-to-top");
    $(window).on("scroll", function() {
        var offsetHeight = document.body.offsetHeight;
        var scrollHeight = document.body.scrollHeight;
        var navbarFixed = scrollHeight > offsetHeight && scrollHeight - offsetHeight > 243;
        e.toggleClass("back-to-top-on", window.pageYOffset > 120);
        if (navbarFixed) {
            $(".navbar").toggleClass("navbar-fixed", window.pageYOffset > 120);
            if (!$("#navbar-title").length && window.pageYOffset > 120 && window.innerWidth > 1280 && $(".entry-title").length == 1) {
                $(".navbar-nav").prepend('<li id="navbar-title"><a>' + $(".entry-title").text() + '</a></li>');
            }
            if ($("#navbar-title").length && window.pageYOffset < 120) {
                $("#navbar-title").remove();
            }
        }
        // scrollbar
        if ($(".scrollbar").length) {
            var a = $(window).scrollTop();
            var b = $(window).height();
            var c = $(document).height();
            if (c === b) {
                $(".scrollbar").width("100%");
            }else {
                $(".scrollbar").width(a / (c - b) * 100 + "%");
            }
        }
    });
    e.click(function() {
        window.scrollTo(0, 0);
    });
    $('[data-fancybox]').fancybox({
        protect: true
    });
    $(".entry-comment").click(function() {
        var dsq = document.createElement('script');
        var thr = document.getElementById('comment_thread')
        dsq.type = 'text/javascript';
        dsq.src = 'https://utteranc.es/client.js';
        dsq.setAttribute('repo', 'honmaple/honmaple.github.io');
        dsq.setAttribute('issue-term', thr.getAttribute('issue-term'));
        dsq.setAttribute('label', 'comment');
        dsq.setAttribute('theme', 'github-light');
        dsq.setAttribute('crossorigin', 'anonymous');

        thr.appendChild(dsq);
        $(".entry-comment").parent().fadeToggle(800);
    });
    $(".navbar-logo").error(function() {
        var content = '<span class="navbar-title">' + $(".navbar-logo").attr("alt") + '</span>';
        $(".navbar-logo").parent().html(content);
    });
    $(".encrypt-container .encrypt-form input").keyup(function(e){
      if(e.keyCode == 13) {
        decrypt($(this).get(0));
      }
    });
    $(".encrypt-container .encrypt-form i").click(function(e) {
        decrypt($(this).get(0));
    });
});
function ISPC() {
    var userAgentInfo = navigator.userAgent;
    var agents = ["Android", "iPhone", "SymbianOS", "Windows Phone", "iPad", "iPod"];
    var flag = true;
    for (var v = 0; v < agents.length; v++) {
        if (userAgentInfo.indexOf(agents[v]) > -1) {
            flag = false;
            break;
        }
    }
    return flag;
}
function Cover() {
    if (!String.prototype.format) {
        String.prototype.format = function () {
            var args = arguments;
            return this.replace(/{(\d+)}/g, function (match, number) {
                return typeof args[number] != 'undefined' ? args[number] : match;
            });
        };
    };
    if ($(window).width() > 768) {
        $.ajax({
            type: "GET",
            url: "https://poem.honmaple.com/api/poem/random",
            dataType: "json",
            success: function (response) {
                var title = '<h3>{0}</h3>'.format(response.data.title);
                var author = '<p>{0}</p>'.format(response.data.author);
                var paragraphs = '<div>{0}</div>'.format(response.data.paragraphs.map(function (item) {
                    return "<p>{0}</p>".format(item);
                }).join(""));
                $(".entry-cover > .entry-cover-right").fadeOut(500, function () {
                    $(this).html(title + author + paragraphs).fadeIn(500);
                });
            }
        });
    };

    function scrollDown() {
        if ($(window).scrollTop() < $(window).height() && !$(".entry-cover").is(":hidden")) {
            $(".entry-cover-left").fadeOut(300);
            $(".entry-cover").slideUp(600);
        }
    }
    $(document).keydown(function (e) {
        if (e.keyCode == 40) { scrollDown(); }
        if (e.keyCode == 34) { scrollDown(); }
    });
    $(window).on('wheel', function (event) {
        if (event.originalEvent.deltaY > 0) {
            scrollDown();
        }
    });
    $("#entry-cover").click(function () {
        $(".entry-cover-left").fadeOut(600);
        $(".entry-cover").slideUp(600);
    });
    var startPos = {
        Top:-1,
        Y:0
    };
    $("body").on('touchstart', function(e) {
        startPos = {
            Top: $(window).scrollTop(),
            Y: e.originalEvent.changedTouches[0].pageY
        };
    });
    $("body").on("touchend", function(e) {
        var pageY = e.originalEvent.changedTouches[0].pageY - startPos.Y;
        if (pageY  < 0) {
            scrollDown();
        }
    });
}

Cover();
